mapper.update:
UPDATE BOARD
SET DROP_DATE = SYSDATE
WHERE BOARD_ID = #{boardId}

UPDATE BOARD
SET TITLE = #{title}, CONTENT = #{content}, UPDATE_DATE = SYSDATE
WHERE BOARD_ID = #{boardId}

mapper.insert:
INSERT INTO BOARD_FILE VALUES(file_seq.nextval,
							  (SELECT board_id FROM (
								SELECT board_id FROM BOARD ORDER BY board_id DESC
								) WHERE ROWNUM = 1),
							   #{boardFile.path},
							   #{boardFile.originName},
							   #{boardFile.updateName}
)
