mapper.select:
SELECT * FROM ( SELECT
	board_id, title, author, content, view_count, create_date, drop_date, ROW_NUMBER() OVER(ORDER BY create_date DESC) AS rn
	FROM BOARD b WHERE DROP_DATE IS NULL) WHERE rn BETWEEN #{pi.offset} AND #{pi.limit}

mapper.update:
UPDATE BOARD
SET DROP_DATE = SYSDATE
WHERE BOARD_ID = #{boardId}
